import React from 'react';
import { View, Text, StyleSheet,  TouchableOpacity, Button, TextInput} from 'react-native';
import { useNavigation } from '@react-navigation/native';

import Container from '../components/background';

const LoginScreen = () => {
    
    const navigation = useNavigation();

    return(      
        <Container>
            <View style = {styles.header}>
                <Text style={styles.text}>This is bank app</Text>
            </View>
            <View style={styles.textContainer}>
                <Text style={styles.text}>Welcome there</Text>
            </View>
            <View style = {styles.inputContainer}>
                <TextInput
                    style={styles.input}
                    placeholder= 'username'
                    placeholderTextColor={'#B5B7BA'}
                />
                <TextInput
                    style={styles.input}
                    placeholder='password'
                    placeholderTextColor={'#B5B7BA'}
                    secureTextEntry= {true}
                />
            </View>
            <View style={styles.buttonContainer}>
                <TouchableOpacity style={styles.button} onPress={() => navigation.navigate("Home")}>
                    <Text style={{color: '#dedfe0', fontSize:16,}}>Login </Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button} onPress={() => navigation.navigate("SignUp")}>
                    <Text style={{color: '#dedfe0', fontSize:16,}}> Register Now</Text>
                </TouchableOpacity>     
                <TouchableOpacity style={styles.button} onPress={() => navigation.navigate("Test")}>
                    <Text style={{color: '#dedfe0', fontSize:16,}}> Test Screen</Text>
                </TouchableOpacity>       
            </View>
        </Container>
    );
};

const styles = StyleSheet.create({
    header: {
        backgroundColor: '#23476b',
        alignItems: 'center',
        justifyContent:'center',
        padding: 20,
    },
    textContainer: {
        flex: 0.5,
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputContainer: {
        flex: 0.45,
        alignItems: 'center',
        // backgroundColor: 'black',
        paddingLeft: 25,
        paddingRight: 25,
    },
    buttonContainer: {
        // backgroundColor: 'blue',
        flex: 0.5,
        alignItems: 'center',
        justifyContent: 'flex-start',
        paddingBottom: '10%',
        paddingRight: 20,
        paddingLeft: 20,
    },
    text: {
        color: '#dedfe0',
        fontSize: 20,  
    },
    button: {
        justifyContent: 'center',
        alignItems: 'center',
        height: 42,
        width: 125,
        backgroundColor: '#05445E',
        borderRadius: 15,
        marginBottom: 15,
    },
    input: {
        backgroundColor: '#54A1C3',
        width: '90%',
        color: '#b4edff',
        padding: 15,
        borderRadius: 15,
        marginTop: 20,
        fontSize: 16,
    },
});

export default LoginScreen;
