import React, { Component, useRef, useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity} from 'react-native';
import Icon from 'react-native-vector-icons/MaterialIcons';
import { useNavigation } from '@react-navigation/native';

import TopBar from '../components/topbar';
import BottomBar from '../components/bottomBar';
import ExpoCamera from '../components/cameraComponent';

const RegistrationScreen =() => {
    
    const navigation = useNavigation();
    
    return(
        <View style={styles.container}>
            <View style={styles.top}>
                <TopBar />
            </View>
            <View style={styles.body}>
                <View style={styles.block1}>
                    <TouchableOpacity>
                        <Icon 
                            name = 'chevron-left'
                            size={30}
                            color='#C2DFF4'
                        />
                    </TouchableOpacity>
                </View>
                <View style={styles.block2}>
                    <ExpoCamera />
                </View>
                <View style={styles.block3}>
                    <TouchableOpacity onPress={() => navigation.navigate("Congrats")} style={styles.buttonConfirm}>
                        <Text style={styles.text}>Confirm</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.buttonRetake}>
                        <Text style={styles.text}>Retake</Text>
                    </TouchableOpacity>
                </View>
            </View>
            <View style={styles.bottom}>
                <BottomBar />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        height:'100%',
        width:'100%',
        backgroundColor: '#3E7297',
    },
    top: {
        flex:0.80,
    },
    body: {
        flex:8.2,
        padding: 20,
    },
    bottom: {
        flex: 1,
    },
    camera: {
        flex: 1,
        borderRadius: 20,
        backgroundColor: 'transparent',
    },
    block1: {
        flex: 0.2,
    },
    block2: {
        flex: 0.5,
        backgroundColor:'blue',
        borderRadius: 15,
    },
    block3: {
        flex: 0.25,
        paddingLeft: 10,
        marginTop: 10,
        paddingRight: 10,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
    },
    buttonConfirm: {
        justifyContent: 'center',
        alignItems: 'center',
        color:'white',
        height: '25%',
        width: 100,
        backgroundColor: '#25954C',
        padding: 10,
        borderRadius: 15,
    },
    buttonRetake: {
        justifyContent: 'center',
        alignItems: 'center',
        color:'white',
        height: '25%',
        width: 100,
        backgroundColor: '#952925',
        padding: 10,
        borderRadius: 15,
    },
    text: {
        color: '#C2DFF4',
    }
});

export default RegistrationScreen;
