import React from 'react';
import { View, Text, StyleSheet, Button, TextInput} from 'react-native';

const LoginScreen = () => {


    return(
        <View style = {styles.container}>
            <View style = {styles.container}>
                <View style = {styles.box1}>
                    <Text style={styles.text}>Bank_App</Text>
                </View>
                <View style = {styles.box2}>
                    <Text style={styles.text}>Hi there!</Text>
                    <TextInput
                        style={styles.input}
                        placeholder= 'username'
                        placeholderTextColor={'#B5B7BA'}
                    />
                    <TextInput
                        style={styles.input}
                        placeholder='password'
                        placeholderTextColor={'#B5B7BA'}
                        secureTextEntry= {true}
                    />
                </View>
                <View style = {styles.box3}>
                    {/* <View style={styles.buttonContainer}> */}
                        <Button   
                            title='Login'
                            color={'blue'}           
                        />
                    {/* </View> */}
                </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#3E7297',
        width: "100%",
        paddingLeft: 20,
        paddingRightt: 20,
    },
    box1: {
        flex: 1,
        alignItems: 'flex-start',
        justifyContent: 'flex-end',  
    },
    box2: {
        flex: 2,
        justifyContent: 'center',
    },
    box3: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        paddingBottom: '10%',
    },
    text: {
        paddingLeft: 5,
        fontSize: 26,
        color: '#F5F5F4',
    },
    buttonContainer: {
        backgroundColor: '#05445E',
    },
    button: {
        borderRadius: 45,
    },
    input: {
        backgroundColor: '#54A1C3',
        width: '90%',
        color: '#b4edff',
        height: '200',
        padding: 10,
        borderRadius: 15,
        marginTop: 20,
        fontSize: 16,
    },
});

export default LoginScreen;
